import * as i0 from "@angular/core";
import * as i1 from "../teacher.service";
import * as i2 from "@angular/router";
import * as i3 from "@angular/forms";
export class TeacherComponent {
    constructor(teacherService) {
        this.teacherService = teacherService;
    }
    ngOnInit() {
        this.getMyStudents();
    }
    getMyStudents() {
        let params = new URLSearchParams(window.location.search);
        let userID = params.get('tid');
        console.log('user is ' + userID);
        this;
    }
    onSubmit(f) {
        console.log('form clicked, Username: ' + f.value['username'] + ' ' + 'Password: ' + f.value['password']);
        this.addStudent(f.value['username'], f.value['password']);
    }
    // making a post to add student into the database
    async addStudent(username, password) {
        let student = { 'email': username, 'password': password };
        let options = {
            method: "POST",
            body: JSON.stringify({ query: student }),
            headers: {
                'Content-Type': 'application/json'
            }
        };
        let params = new URLSearchParams(window.location.search);
        let teacherID = params.get('tid');
        let responseObj = await fetch(`/api/v1/newStudent/${teacherID}`, options);
        let data = await responseObj.json();
        console.log(data);
    }
}
