import * as i1 from "../teacher.service";
import * as i2 from "@angular/forms";
export class AddStudentComponent {
    constructor(teacherService) {
        this.teacherService = teacherService;
    }
    ngOnInit() {
        this.roleSelected = 'Student';
        let url = window.location.href.split('/');
        for (let i = 0; i < url.length; i++) {
            if (url[i] == 'teacher') {
                this.teacher = url[i + 1];
                console.log('username is ' + this.teacher);
            }
        }
        ;
    }
    onRoleSelected(val) {
        this.roleSelected = val;
        console.log(this.roleSelected);
    }
    onSubmit(f) {
        console.log(f.value['roleSelect']);
        if (f.value['roleSelect'] == 'Teacher') {
            //console.log('adding teacher');
            this.addTeacher(f.value.firstname, f.value.lastname, f.value['username'], f.value['password']);
        }
        else if (f.value['roleSelect'] == 'Student') {
            console.log('adding student');
            this.addStudent(f.value.firstname, f.value.lastname, f.value['username'], f.value['password']);
        }
        console.log('form clicked, Name: ' + f.value.firstname + ' ' + f.value.lastname + ' Username: ' + f.value['username'] + ' ' + 'Password: ' + f.value['password']);
        //this.addStudent(f.value.firstname, f.value.lastname, f.value['username'], f.value['password']);
    }
    // making a post to add student into the database
    async addStudent(first, last, username, password) {
        this.teacherService.addStudent(first, last, username, password, this.teacher);
        //window.location.href = '/teacher/'+this.teacher;
    }
    async addTeacher(first, last, username, password) {
        console.log('adding teacher method');
        this.teacherService.addTeacher(first, last, username, password);
    }
}
