import * as i1 from "../movie.service";
import * as i2 from "../student-header/student-header.component";
export class StudentMovieSearchComponent {
    constructor(movieService) {
        this.movieService = movieService;
    }
    ngOnInit() {
    }
    onSubmit(f) {
        //this.searchMovie(f.value['title']);
    }
    async searchMovie(title) {
        this.movieService.getMovie(title)
            .subscribe(movie => {
            if (Object.keys(movie).length < 4) {
                console.log('movie not in db');
                alert("This movie isn't in the database :( ");
            }
            else {
                console.log(movie);
                this.movies = movie;
                let titleCopy = this.movies['Title'];
                console.log(titleCopy);
                titleCopy = titleCopy.replace(/ /g, '+');
                window.location.hash = titleCopy;
            }
        });
    }
    review() {
        let hash = window.location.hash;
        let user;
        let url = window.location.href.split('/');
        let role;
        console.log(url);
        for (let i = 0; i < url.length; i++) {
            console.log(url[i]);
            if (url[i] == 'teacher') {
                user = url[i + 1];
                console.log('username is ' + user);
                role = 'teacher';
                this.teacher = url[i + 1];
            }
            else if (url[i] == 'student') {
                user = url[i + 1];
                role = 'student';
                this.student = url[i + 1];
            }
        }
        ;
        hash = hash.replace('#', '');
        window.location.href = '/' + role + '/' + user + '/review?title=' + hash;
    }
}
