import { __decorate } from "tslib";
import { Component } from '@angular/core';
let TeacherComponent = class TeacherComponent {
    constructor() { }
    ngOnInit() {
    }
    onSubmit(f) {
        console.log('form clicked, Username: ' + f.value['username'] + ' ' + 'Password: ' + f.value['password']);
        this.addStudent(f.value['username'], f.value['password']);
        //this.testMovie();
    }
    async addStudent(username, password) {
        let student = { 'email': username, 'password': password };
        let options = {
            method: "POST",
            body: JSON.stringify({ query: student }),
            headers: {
                'Content-Type': 'application/json'
            }
        };
        let params = new URLSearchParams(window.location.search);
        let teacherID = params.get('tid');
        //let responseObj = await fetch('/api/v1/newStudent', options );
        let responseObj = await fetch(`/api/v1/newStudent/${teacherID}`, options);
        let data = await responseObj.json();
        console.log(data);
    }
};
TeacherComponent = __decorate([
    Component({
        selector: 'app-teacher',
        templateUrl: './teacher.component.html',
        styleUrls: ['./teacher.component.css']
    })
], TeacherComponent);
export { TeacherComponent };
