import * as i0 from "@angular/core";
import * as i1 from "../teach-header/teach-header.component";
import * as i2 from "@angular/forms";
export class AddStudentComponent {
    constructor() { }
    ngOnInit() {
    }
    onSubmit(f) {
        console.log('form clicked, Name: ' + f.value.firstname + ' ' + f.value.lastname + ' Username: ' + f.value['username'] + ' ' + 'Password: ' + f.value['password']);
        this.addStudent(f.value.firstname, f.value.lastname, f.value['username'], f.value['password']);
    }
    // making a post to add student into the database
    async addStudent(first, last, username, password) {
        let student = { 'name': first + ' ' + last, 'email': username, 'password': password };
        let options = {
            method: "POST",
            body: JSON.stringify({ query: student }),
            headers: {
                'Content-Type': 'application/json'
            }
        };
        let params = new URLSearchParams(window.location.search);
        let teacher = params.get('username');
        let responseObj = await fetch(`/api/v1/newStudent/${teacher}`, options);
        let data = await responseObj.json();
        console.log(data);
        window.location.href = '/teacher?username=' + teacher;
        if (Object.keys(data)[0] == 'user') {
            alert('An Account with this email already exists!');
        }
    }
}
